import express from "express";
import userRoutes from "./userRoutes.js";
import movieRoutes from "./movieRoutes.js";
import showRoutes from "./showRoutes.js";
import screenRoutes from "./screenRoutes.js";
import theaterRoutes from "./theaterRoutes.js";
import bookingRoutes from "./bookingRoutes.js";
import authRoutes from "./auth.js";
import paymentRoutes from "./paymentRoutes.js";
import stripeRoutes from "./stripeRoutes.js";
import esewaRoutes from "./esewaRoutes.js";
import notificationRoutes from "./notificationRoutes.js";
const router = express.Router();
router.use("/user", userRoutes);
router.use("/movie", movieRoutes);
router.use("/show", showRoutes);
router.use("/screen", screenRoutes);
router.use("/theater", theaterRoutes);
router.use("/booking", bookingRoutes);
router.use("/auth", authRoutes);
router.use("/payment", paymentRoutes);
router.use("/stripe", stripeRoutes);
router.use("/esewa", esewaRoutes);
router.use("/notifications", notificationRoutes);
export default router;
